mapper.select:
SELECT *
  FROM USERS
 WHERE EMAIL = #{param1}
   AND PASSWORD = #{param2}

mapper.update:
UPDATE USERS
   SET PASSWORD = #{param2}
 WHERE EMAIL = #{param1}

