mapper.update:
UPDATE USERS
   SET PASSWORD = #{param1},
   	   USER_NICKNAME = #{param2},
   	   LOCATION1_NO = #{param3}
   	   LOCATION2_NO = #{param4}
 WHERE USER_NO = #{user_no}

UPDATE USERS
   SET PROFILE_PHOTO = #{param2}
 WHERE USER_NO = #{param1}

