mapper.select:
SELECT MAX(MEETING_NO)
FROM MEETING
WHERE USER_NO = #{param1}

SELECT *
FROM MATERIALS
WHERE MEETING_NO = #{param1}

mapper.update:
UPDATE MEETING
SET MEETING_HIT = MEETING_HIT + 1
WHERE MEETING_NO = #{param1}

UPDATE MEETING
SET MEETING_TITLE = #{meeting_title}, MEETING_DATE = #{meeting_date}, START_GATHER_DATE = #{start_gather_date}, END_GATHER_DATE = #{end_gather_date}, MEETING_MIN = #{meeting_min}, MEETING_MAX = #{meeting_max}, EXERCISE_NO = #{exercise_no}, LOCATION1_NO = #{location1_no}, LOCATION2_NO = #{location2_no}, DETAIL_LOCATION = #{detail_location}, MEETING_CONTENT = #{meeting_content}
WHERE MEETING_NO = #{meeting_no}

UPDATE MATERIALS
SET MATERIALS_NAME = #{param1}
WHERE MEETING_NO = #{param2}

UPDATE PHOTO
SET PHOTO_FILENAME = #{param1}
WHERE PHOTO_REFERER_NO = #{param2}
